mapper.select:
@beginningOfMonth = convert(date_add(date_add(last_day(#{nowTime}),interval 1 day),interval -1 month), DATETIME);

select * from DataRecord where id = #{id, jdbcType=INTEGER} and
    startTime between @beginningOfMonth and #{nowTime} order by startTime;

@beginningOfMonth = convert(date_add(date_add(last_day(#{nowTime}),interval 1 day),interval -1 month), DATETIME);

select * from SMSRecord where id = #{id, jdbcType=INTEGER} and
    sendTime between @beginningOfMonthand and #{nowTime} order by sendTime;

@beginningOfMonth = convert(date_add(date_add(last_day(#{nowTime}),interval 1 day),interval -1 month), DATETIME);
select * from CallRecord where id = #{id, jdbcType=INTEGER} and
    startTime between @beginningOfMonth and #{nowTime} order by startTime;
