Create Table IF NOT EXISTS Order1 (
    `id` int,
    `pid` INT,
    `orderTime` DATETIME
);

Create Table IF NOT EXISTS Cancel (
    `id` INT,
    `pid` INT,
    `cancelTime` DATETIME,
    `EffectType` varchar(20)
);

Create Table IF NOT EXISTS `SMSRecord` (
    `id` INT,
    `number` INT,
    `sendTime` DATETIME
);

Create Table IF NOT EXISTS UserPlan (
    `id` INT,
    `pid` INT
);

Create Table IF NOT EXISTS `CallRecord` (
    `id` INT,
    `number` int,
     `startTime` DATETIME,
    `endTime` DATETIME
);
